################################################################################
# 파일리스트 소트
################################################################################

function n_sort($file_list) {
	$filelist_sort = array();
	foreach($file_list as $sort_file){
		$n_sort = str_replace("화.zip", "", strtolower($sort_file));
		$n_sort = str_replace("권.zip", "", strtolower($n_sort));
		$n_sort = str_replace("화.cbz", "", strtolower($n_sort));
		$n_sort = str_replace("권.cbz", "", strtolower($n_sort));
		$n_sort = str_replace(".zip", "", strtolower($n_sort));
		$n_sort = str_replace(".cbz", "", strtolower($n_sort));
		$n_sort = str_replace(" ", "_", strtolower($n_sort));
		$n_sort = str_replace("_imgfolder", "", strtolower($n_sort));
		$filelist_sort[$sort_file] = $n_sort;
	}
	asort($filelist_sort, SORT_NATURAL);
	$file_list = array_keys($filelist_sort);
	return $file_list;
}

################################################################################
# 파일리스트 소트 - 제목과 관계없이
################################################################################

function arr_sort($array, $key, $sort){
	$keys = array();
	$vals = array();
	foreach ($array as $k=>$v){
		$i = $v[$key].'.'.$k;
		$vals[$i] = $v;
		array_push($keys, $k);
	}
	unset($array);
	if ($sort=='asc') {
		ksort($vals);
	} else {
		krsort($vals);
	}
	$ret = array_combine($keys, $vals);
	unset($keys);
	unset($vals);
	return $ret;
}

################################################################################
# 접근권한이 있는지 확인 및 리모트폴더 여부 반환
################################################################################

function dir_check($getdir) {
	global $base_dir;
	$rootdir = array();
	$rootdir = explode("/", $getdir);
	$getmodefile = $base_dir."/".$rootdir[1].".json";
	if(is_file($getmodefile) == true) {
		$dirmode_arr = array();
		$dirmode_arr = json_decode(file_get_contents($getmodefile), true);
		if($dirmode_arr[$_SESSION["user_group"]] !== 1) {
			echo "권한이 없습니다. 3초 후 초기화면으로 돌아갑니다.<br>";
			echo("<meta http-equiv=\"refresh\" content=\"3; url=index.php\">"); 
			exit();
		} else {
			if($dirmode_arr['remote'] == "y"){
				return "y";
			} elseif($dirmode_arr['remote'] == "n" || $dirmode_arr['remote'] == null){
				return "n";
			}
		}		
	}
}

################################################################################
# 새파일체크여부 반환
################################################################################

function checknew_value($chkdir) {
	global $base_dir;
	$rootdir = array();
	$rootdir = explode("/", $chkdir);
	$getmodefile = $base_dir."/".$rootdir[1].".json";
	if(is_file($getmodefile) == true) {
		$dirmode_arr = array();
		$dirmode_arr = json_decode(file_get_contents($getmodefile), true);
		if($dirmode_arr['checknew'] == "y" || $dirmode_arr['checknew'] == null){
			return "y";
		} elseif($dirmode_arr['checknew'] == "n"){
			return "n";
		}
	}
}


################################################################################
# 긴 제목을 줄여줌
################################################################################

function cut_title($title){
			$nowdir_arr = array();
			$nowdir_arr = explode("/", decode_url($_GET['dir']));
			$nowdir = $nowdir_arr[count($nowdir_arr) - 1];
			$nowdir = strtolower($nowdir);
			$title = strtolower($title);
			$title = str_replace(".zip", "", $title);
			$title = str_replace(".cbz", "", $title);
			$divider = array("|","｜"," l ","│","%7c");
			foreach($divider as $div){
				$title = substr($title, strpos($title, $div));
				$title = str_replace($div, "", $title);
			}
			$title = str_replace("%3f", "?", $title);
			$title = str_replace("(decensored)", "-무수정", $title);
			$title = preg_replace("/\[[^]]*\]/","",$title);
			$nowdir = preg_replace("~\(.*\)~","",$nowdir);
			$nowdir = preg_replace("/\[[^]]*\]/","",$nowdir);
			if($nowdir != null){
				$title = substr($title, strpos($title, $nowdir));
			}
			$title = str_replace($nowdir, "", $title);
			if(strlen($title) > 10){
				$title = preg_replace("~\(.*\)~","",$title);
			}
			return $title;
}

################################################################################
# Return encode/decode URL for Special character
################################################################################

function encode_url($filename) {
	$filename = str_replace("+", "{pl}", $filename);
	$filename = str_replace(".", "{dt}", $filename);
	$filename = str_replace("%", "{pc}", $filename);
	$filename = urlencode($filename);
	return $filename;
}

function decode_url($filename) {
	$filename = urldecode($filename);
	$filename = str_replace("{pl}", "+", $filename);
	$filename = str_replace("{dt}", ".", $filename);
	$filename = str_replace("{pc}", "%", $filename);
	return $filename;
}

################################################################################
# Return MIME Content type
################################################################################

function mime_type($filename) {

        $mime_types = array(

            'txt' => 'text/plain',
            'htm' => 'text/html',
            'html' => 'text/html',
            'php' => 'text/html',
            'css' => 'text/css',
            'js' => 'application/javascript',
            'json' => 'application/json',
            'xml' => 'application/xml',
            'swf' => 'application/x-shockwave-flash',
            'flv' => 'video/x-flv',

            // images
            'png' => 'image/png',
            'jpe' => 'image/jpeg',
            'jpeg' => 'image/jpeg',
            'jpg' => 'image/jpeg',
            'gif' => 'image/gif',
            'bmp' => 'image/bmp',
            'ico' => 'image/vnd.microsoft.icon',
            'tiff' => 'image/tiff',
            'tif' => 'image/tiff',
            'svg' => 'image/svg+xml',
            'svgz' => 'image/svg+xml',

            // archives
            'zip' => 'application/zip',
            'rar' => 'application/x-rar-compressed',
            'exe' => 'application/x-msdownload',
            'msi' => 'application/x-msdownload',
            'cab' => 'application/vnd.ms-cab-compressed',

            // audio/video
            'mp3' => 'audio/mpeg',
            'qt' => 'video/quicktime',
            'mov' => 'video/quicktime',

            // adobe
            'pdf' => 'application/pdf',
            'psd' => 'image/vnd.adobe.photoshop',
            'ai' => 'application/postscript',
            'eps' => 'application/postscript',
            'ps' => 'application/postscript',

            // ms office
            'doc' => 'application/msword',
            'rtf' => 'application/rtf',
            'xls' => 'application/vnd.ms-excel',
            'ppt' => 'application/vnd.ms-powerpoint',

            // open office
            'odt' => 'application/vnd.oasis.opendocument.text',
            'ods' => 'application/vnd.oasis.opendocument.spreadsheet',
        );

        $ext = strtolower(array_pop(explode('.',$filename)));
        if (array_key_exists($ext, $mime_types)) {
            return $mime_types[$ext];
        }
        elseif (function_exists('finfo_open')) {
            $finfo = finfo_open(FILEINFO_MIME);
            $mimetype = finfo_file($finfo, $filename);
            finfo_close($finfo);
            return $mimetype;
        }
        else {
            return 'application/octet-stream';
        }
}

